  # Vector2, 2 components
  class Vector2 < FFI::Struct
    layout(
      :x, :float, # Vector x component
      :y, :float, # Vector y component
    )
    def x = self[:x]
    def x=(v) self[:x] = v end
    def y = self[:y]
    def y=(v) self[:y] = v end
  end

